var env = (c) => {
  const global = globalThis;
  if (c.runtime === "bun" || c.runtime === "node" || c.runtime === "edge-light" || c.runtime === "lagon") {
    return global?.process?.env;
  }
  if (c.runtime === "deno") {
    return Deno.env.toObject();
  }
  if (c.runtime === "workerd") {
    return c.env;
  }
  if (c.runtime === "fastly") {
    return {};
  }
  return {};
};
export {
  env
};
